mapper.insert:

  SELECT LAST_INSERT_ID()

insert into t_user (create_time, update_time, create_person, update_person, mobile_phone, 
  email, passwd, status, openid_weixin, last_login, certification
  )
values (#{createTime}, #{updateTime}, #{createPerson}, #{updatePerson}, #{mobilePhone}, 
  #{email}, #{passwd}, #{status}, #{openidWeixin}, #{lastLogin}, #{certification}
  )

mapper.select:
select   from t_user

  is_delete=0 

     and create_time = #{createTime}


     and update_time = #{updateTime}


     and create_person = #{createPerson}


     and update_person = #{updatePerson}


     and mobile_phone = #{mobilePhone}


     and email = #{email}


     and passwd = #{passwd}


     and status = #{status}


     and openid_weixin = #{openidWeixin}


     and last_login = #{lastLogin}


     and certification = #{certification}
